const LOAD_ONE_BIZ = 'business/loadOneBiz';

const loadOneBusiness = (oneBusiness) => ({
  type: LOAD_ONE_BIZ,
  oneBusiness,
})

export const getOneBusiness = (id) => async (dispatch) => {
  const response = await fetch(`/api/businesses/${id}`)
  const {business, photos} = await response.json();

  dispatch(loadOneBusiness(business, photos));
  return (response, business, photos)

}

const initialState = {};

const oneBusinessReducer = (state = initialState, action) => {
  switch (action.type) {

    case LOAD_ONE_BIZ:
      const newState = { ...state };
        newState[action.oneBusiness.id] = action.oneBusiness;
      return newState;
    default:
      return state;
  }
};

export default oneBusinessReducer;
