import {  useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addBusiness } from '../../store/businesses';
import LoginForm from '../LoginFormModal';
import './AddBusiness.css'


const AddBusiness = () => {
  const dispatch = useDispatch();
  const sessionUser = useSelector(state => state.session.user);
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [stars, setStars] = useState("");
  const [category, setCategory] = useState("");
  const [isOpen, setIsOpen] = useState('true');
  const [longitude, setLongitude] = useState('');
  const [latitude, setLatitude] = useState('');
  console.log(sessionUser)

  if(!sessionUser) {
      <LoginForm/>
  } else {


  const reset = () => {
    setName("");
    setAddress("");
    setStars("");
    setCategory("");
    setIsOpen('true')
    setLongitude("");
    setLatitude("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newBusiness = {
      name,
      address,
      stars,
      category,
      isOpen,
      longitude,
      latitude
    };

    dispatch(addBusiness(newBusiness));
    reset();
  };
  }



    return (
            <>
            <div>
                <form>
                  <ul>
                  </ul>

                  <div className="name-fields">              
                    <label className="signup-label firstName">
                      First Name
                      <input
                        className="signup-input"
                        type="text"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        required
                      />
                    </label>
                    <label className="signup-label lastName">
                      Last Name
                      <input
                        className="signup-input"
                        type="text"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        required
                      />
                    </label>
                  </div>
                  <label className="signup-label">
                    Email
                    <input 
                      className="signup-input"
                      type="text"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                    />
                  </label>
                  <label className="signup-label">
                    Profile Image
                    <input
                      className="signup-input"
                      type="text"
                      value={profileImg}
                      onChange={(e) => setProfileImg(e.target.value)}
                      required
                    />
                  </label>
                  <label className="signup-label">
                    Password
                    <input
                      className="signup-input"
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </label>
                  <label className="signup-label">
                    Confirm Password
                    <input
                      className="signup-input"
                      type="password"
                      value={confirmPassword}
                      onChange={(e) => setConfirmPassword(e.target.value)}
                      required
                    />
                  </label>
                  <div className="buttons">
                    <button className="signup-button" type="submit">Sign Up</button>
                    <button className="demo-button" type="submit">Demo</button>
                  </div>
                </form>
                <img className="signupImage" src="https://i.imgur.com/qI2NnYR.png" alt="placeholder"></img>
            </div>
            <div className="spacer"></div>

    </> 
    );


};

export default AddBusiness;
